"""Set of the Unicode blocks; ``(start: int, end: int, name: str)``."""

BLOCKS_BYNAME: dict[str, tuple[str, str, int]] = {x[2]: (x[0], x[1], x[3]) for x in BLOCKS}
"""Dictionary of the Unicode blocks; ``{name}: (start, end, reserved)``."""

CSUR_BYNAME: dict[str, tuple[str, str, int]] = {x[2]: (x[0], x[1], 0) for x in CSUR}
"""Dictionary of the CSUR blocks; ``{name}: (start, end, 0)``."""


def parse_blocks_txt(blocks_txt: str) -> list[tuple[str, str, str, int]]:
    """
    Parse the Blocks.txt found at https://unicode.org/Public/UNIDATA/Blocks.txt::

        0000..007F; Basic Latin
        0080..00FF; Latin-1 Supplement
        0100..017F; Latin Extended-A
        0180..024F; Latin Extended-B
        0250..02AF; IPA Extensions
        ...

    :param blocks_txt: The contents of Blocks.txt as a string.
    :return: A set of tuples of block information.
    """
    blocks = []
    for line in blocks_txt.splitlines():
        if not line or line[0] == "#":
            continue
        split = line.split("..", 1)
        half = split[1].split("; ", 1)
        start = int(split[0], 16)
        end = int(half[0], 16)
        unallocated = 0
        for codepoint in range(start, end + 1):
            char = chr(codepoint)
            if unicodedata.name(char, None) is None:
                unallocated += 1
        blocks.append((chr(start), chr(end), half[1], unallocated))
    return blocks


def block(character: str, default: str = "No Block", include_csur: bool = False) -> str:
    """
    Returns the block assigned to `character` as a string.
    Otherwise, return `default`.

    >>> block("A")
    'Basic Latin'
    >>> block("\u265c")
    'Miscellaneous Symbols'
    >>> block("\ue545", include_csur = True)
    'Rynnan'

    :param character: The character.
    :param default: What to return if `character` is not in a block, defualt is "No Block"
    :param include_csur:
        If True, `CSUR <https://www.evertype.com/standards/csur/>`_ blocks will also be included.
        If False, CSUR blocks will be ignored.
    :return:
        Unicode block that `character` belongs to.
        If `character` does not belong to any block, `default` will be returned.
    """
    if include_csur:
        scan = BLOCKS + CSUR
    else:
        scan = BLOCKS
    for line in scan:
        if line[0] <= character <= line[1]:
            return line[2]
    return default
